import React, { useContext, useState, useEffect } from "react";
import MovieList from "../../api/MovieList";
import { MovieContext } from "../../context/MovieContext";
import { useNavigate, useParams } from "react-router-dom";

export default function VideoPlayer() {
  const { id } = useParams();
  const nav = useNavigate();

  const { movies, setMovies } = useContext(MovieContext);
  const [actors, setActors] = useState([]);
  const [director, setDirector] = useState([]);
  const [comments, setComments] = useState("");
  const [firstname, setFirstname] = useState("");
  const [userComments, setUserComments] = useState([]);
  const [date, setDate] = useState("");
  const [genreid, setGenreid] = useState("");
  const [genreName, setGenreName] = useState("");

  const fetchData = async () => {
    try {
      const video = await MovieList.get(`/get-movie-list/${id}`);
      setMovies(video.data);
      setGenreid(video.data?.movie_title?.genreid);

      const adetail = await MovieList.get(`/actor-details/${id}`);
      setActors(adetail.data.info);

      const ddetail = await MovieList.get(`/director-details/${id}`);
      setDirector(ddetail.data.info);

      const user = await MovieList.get(`/get-movie-comment/${id}`);
      setUserComments(user.data.info);

      const gen = await MovieList.get(`/get-genre/${genreid}`);
      setGenreName(gen?.data);

    } catch (error) {
      console.log(error.message);
      // Handle error
    }
  };

  const addComment = async (e) => {
    e.preventDefault();
    try {
      await MovieList.post(
        `/add-comments/${movies?.data?.movie_title?.videoid}`,
        { name: firstname, comments: comments }
      );
      fetchData(); // Refresh data to show new comment
    } catch (error) {
      console.log(error.message);
    }
  };

  useEffect(() => {
    fetchData();
  }, [id]);

  return (
    <div className="flex flex-col items-center w-full min-h-screen pt-10">
      <div className="w-full max-w-4xl">
        <iframe
          width="1000"
          height="500"
          src={movies?.data?.movie_title?.link}
          title="YouTube video player"
          frameBorder="0"
          allow="accelerometer; autoplay; clipboard-write; encrypted-media; gyroscope; picture-in-picture"
          allowFullScreen
        ></iframe>
      </div>

      <div className="mt-10 text-center">
        <h1 className="font-bold text-4xl dark:text-black">Movie Cast</h1>
      </div>

      <div className="grid grid-cols-2 gap-4 mt-4 max-w-4xl">
        {actors.map((actor) => (
          <div key={actor.actorid} className="block p-6 max-w-sm rounded-lg border border-gray-200 shadow-md dark:border-gray-700 bg-gray-700">
            <div className="flex items-center space-x-4">
              <img className="w-36 h-36 rounded-full" src={actor.aimg_link} alt="" />
              <div className="space-y-1 font-medium dark:text-white">
                <div className="text-white">{actor.fname} {actor.lname}</div>
                <div className="text-white">Gender: {actor.gender}</div>
              </div>
            </div>
          </div>
        ))}
      </div>

      <div className="mt-10 text-center">
        <h1 className="font-bold text-4xl dark:text-black">Director</h1>
      </div>

      <div className="max-w-4xl">
        {director.map((dir) => (
          <div key={dir.directorid} className="block p-6 max-w-sm bg-gray-700 rounded-lg border border-gray-200 shadow-md dark:border-gray-700">
            <div className="flex items-center space-x-4">
              <img className="w-36 h-36 rounded-full" src={dir.dimg_link} alt="" />
              <div className="space-y-1 font-medium dark:text-white">
                <div className="text-white">{dir.fname} {dir.lname}</div>
                <div className="text-white">Gender: {dir.gender}</div>
                <div className="text-white">No Of Movies: {dir.no_of_movies}</div>
              </div>
            </div>
          </div>
        ))}
      </div>

      <p className="pt-10 font-bold text-3xl">Movie Details</p>
      <div className="max-w-4xl">
        <a href="#" className="flex flex-col items-center bg-gray-700 rounded-lg border shadow-md dark:border-gray-700 dark:bg-gray-800 dark:hover:bg-gray-700">
          <img
            className="object-cover w-full h-96 rounded-t-lg md:h-auto md:w-48 md:rounded-none md:rounded-l-lg"
            src={movies?.data?.movie_title?.poster_link}
            alt=""
          />
          <div className="p-4 leading-normal text-center">
            <h5 className="mb-2 text-2xl font-bold tracking-tight text-white">{movies?.data?.movie_title?.title}</h5>
            <p className="mb-3 font-normal text-white">{movies?.data?.movie_title?.movie_desc}</p>
            <div className="flex justify-center space-x-4 text-base">
              <p className="mb-1 font-normal text-5xl text-yellow-600">
                {"*".repeat(movies?.data?.movie_title?.imdb_rating)}
              </p>
              <p className="font-normal text-yellow-600 text-xl">{movies?.data?.movie_title?.imdb_rating}</p>
            </div>
            <p className="text-white">
              Date of Release: {movies?.data?.movie_title?.date_of_release.slice(0, 10)}
            </p>
            <p className="text-white pt-3">Genre: {genreName?.genre_name}</p>
          </div>
        </a>
      </div>

      <div className="max-w-4xl mt-10">
        <p className="text-3xl font-bold">Add Comments</p>
        <form className="pt-4" onSubmit={addComment}>
          <div className="mb-4 w-full bg-gray-50 rounded-lg border border-gray-200 dark:bg-gray-700 dark:border-gray-600">
            <div className="py-2 px-4 bg-white rounded-t-lg dark:bg-gray-800">
              <label htmlFor="firstname" className="sr-only">Your Name</label>
              <input
                type="text"
                value={firstname}
                id="firstname"
                className="px-0 w-full text-sm text-gray-900 bg-white border-0 dark:bg-gray-800 focus:ring-0 dark:text-white dark:placeholder-gray-400"
                placeholder="First Name"
                onChange={(e) => setFirstname(e.target.value)}
                required
              />
            </div>
            <div className="py-2 px-4 bg-white rounded-t-lg dark:bg-gray-800">
              <label htmlFor="comment" className="sr-only">Your comment</label>
              <textarea
                id="comment"
                rows="4"
                value={comments}
                onChange={(e) => setComments(e.target.value)}
                className="px-0 w-full text-sm text-gray-900 bg-white border-0 dark:bg-gray-800 focus:ring-0 dark:text-white dark:placeholder-gray-400"
                placeholder="Write a comment..."
                required
              />
            </div>
            <div className="flex justify-center items-center py-2 px-3 border-t dark:border-gray-600">
              <button
                type="submit"
                className="inline-flex items-center py-2.5 px-4 text-xs font-medium text-center text-white bg-blue-700 rounded-lg focus:ring-4 focus:ring-blue-200 dark:focus:ring-blue-900 hover:bg-blue-800"
              >
                Post comment
              </button>
            </div>
          </div>
        </form>
      </div>

      <p className="text-3xl pt-6 font-bold">Comments</p>
      <div className="p-4 max-w-4xl bg-gray-700 rounded-lg border shadow-md sm:p-8 dark:bg-gray-800 dark:border-gray-700">
        <div className="grid mb-4">
          <ul role="list" className="divide-y divide-gray-200 dark:divide-gray-700">
            {userComments.map((comment) => (
              <li key={comment.id} className="py-3 sm:py-4">
                <div className="flex items-baseline justify-between space-x-4">
                  <div className="flex-1 min-w-0">
                    <p className="text-xl font-medium text-white truncate dark:text-white">{comment.username}</p>
                    <p className="overflow-hidden text-white leading-relaxed">{comment.comment}</p>
                  </div>
                </div>
              </li>
            ))}
          </ul>
        </div>
      </div>
    </div>
  );
}
